CREATE DATABASE store;
USE store;
CREATE TABLE Registration ( Name TEXT NOT NULL , DOB VARCHAR(20) NOT NULL , Email VARCHAR(50) NOT NULL , Mobile_Number BIGINT NOT NULL , Gender TEXT NOT NULL , Address VARCHAR(100) NOT NULL , Password VARCHAR(20) NOT NULL , PRIMARY KEY (Email(20)), UNIQUE (Mobile_Number));
CREATE TABLE Login ( SNO INT AUTO_INCREMENT, UID VARCHAR(50) NOT NULL , Date_Time TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (SNO))AUTO_INCREMENT=1;
CREATE TABLE ShopDetails (ID INT, Shop_Name VARCHAR(30) NOT NULL , Owner_Name VARCHAR(20) NOT NULL , Phone_Number BIGINT NOT NULL , Email VARCHAR(50) NOT NULL , Shop_Address VARCHAR(100) NOT NULL );
