CREATE TABLE ProductRecords ( SU VARCHAR(30), Product_Name VARCHAR(30) NOT NULL , Cost FLOAT NOT NULL, PRIMARY KEY(SU) );
CREATE TABLE CustomerRecords ( Customer_Name VARCHAR(30) NOT NULL , Customer_Phno BIGINT NOT NULL , Customer_Email VARCHAR(50) DEFAULT "N/A", Customer_Address VARCHAR(100) DEFAULT "N/A", Seller VARCHAR(20) NOT NULL , Total_Price FLOAT NOT NULL, Purchase_Time TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, Bill_No INT NOT NULL UNIQUE AUTO_INCREMENT, PRIMARY KEY(Bill_no))AUTO_INCREMENT=100;
CREATE TABLE Bill (Bill_No INT NOT NULL, SU VARCHAR(30),Product_Name VARCHAR(30),  QTY INT, Cost FLOAT, FOREIGN KEY (Bill_No) REFERENCES CustomerRecords (Bill_no)); 